mapper.select:
select fbid, fbtitle, fbwriter, fbcount, flocation from freshman_board where fbstatus='Y'
and flocation=#{location}
order by fbid desc

select count(*) from freshman_board
where fbstatus = 'Y'

and flocation=#{location}

mapper.update:
update freshman_board
set fbcount = fbcount+1
where fbId = #{fbId}

update freshman_board
set fbstatus = 'N'
where fbid = #{fbId}

update attachment
set originalFilename = #{originalFileName},
	renameFileName = #{renameFileName}
where bcode = #{bcode}
and refbid = #{fbId}

update reply
set rStatus='N'
where rid=#{rId}
