mapper.update:
update FREEBOARD
set FRBCOUNT = FRBCOUNT + 1
where FRBID = #{frbId}

update FREEBOARD
set frbtitle=#{frbTitle}, frbcontent=#{frbContent}, FRBMODIFYDATE=sysdate, 
where frbid=#{frbId}

update attachment
set originalFilename = #{originalFileName},
	renameFileName = #{renameFileName}
where bcode = #{bcode}
and refbid = #{frbId}

update FREEBOARD
set FRBSTATUS='N'
where frbid = #{frbId}
